mapper.select:
SELECT USERID, SSN, NAME, ROLL, TEAMID, PASSWORD, AGE, GENDER, SUBJECT 
FROM MEMBER 
WHERE USERID LIKE #{userid}

SELECT USERID, SSN, NAME, ROLL, TEAMID, PASSWORD, AGE, GENDER, SUBJECT
FROM MEMBER
WHERE USERID LIKE #{userid}
AND PASSWORD LIKE #{password}

mapper.update:
UPDATE MEMBER
SET PASSWORD = #{password},
TEAMID = #{teamid},
ROLL = #{roll}
WHERE USERID LIKE #{userid}

mapper.delete:
DELETE FROM MEMBER 
WHERE USERID LIKE #{userid}
AND PASSWORD LIKE #{password}
